import {useState} from 'react';
import Backdrop from './Backdrop';
import Modal from './Modal';

function Todo(props){ //funcation name should start in capital
  const[value,funTochgvalue] = useState(false);//returns an array with 2 elements
                        // value stores current state funTo.. a function which changes the value
  function deleteHandler(){
      funTochgvalue(true);
  } 

  function closeModelHandler(){
      funTochgvalue(false);
  }
  
  return(
    <div >
    <div className='card'>
      <h2>{props.text}</h2>
      <div className="actions">
        <button className="btn" onClick={deleteHandler}>Delete</button>
      </div>
    </div> 
    { value?<Modal onCancel={closeModelHandler} onConf />:null}
    { value && <Backdrop onCancel={closeModelHandler}/>}
  </div>
  );
}

export default Todo; // export to make this fun available outside of this file
