                        // value stores current state funTo.. a function which changes the value
  function deleteHandler(){
      funTochgvalue(true);
  } 

  function closeModelHandler(){
      funTochgvalue(false);
  }
  
  return(
    
    <div> 
        <Login />
        <Backdrop/>
    </div>
  );
}

export default LBrender;
