import Card from "../ui/card";
import classes from './login.module.css';
import loginlogo from './login.png';
import {useRef} from 'react';
import { useNavigate } from "react-router-dom";

function Login(props){
    const navigate = useNavigate();

    const user = useRef();
    const pswd = useRef();

    function submitHandler(event){
        event.preventDefault();

        const User = user.current.value;
        const Pswd = pswd.current.value;

        const Data = {
            Name : User,
            Password : Pswd
        };
        console.log(Data);

        fetch('https://react-9eb50-default-rtdb.firebaseio.com/login.json',
        {
            method:'POST',
            body:JSON.stringify(Data),
            headers:{
                'Content-Type':'application/json'
            }
        }
        ).then(res => {
            return res.json();
        }).then(data=>{
            console.log(data);
            navigate("/all",{replace:true});

        });
    }

    return <Card>
        <img src={loginlogo}  />
        <div class="isa_info">
            <i class="fa fa-info-circle"></i>
            Replace this text with your own text.
        </div>
        <form className={classes.form} onSubmit={submitHandler}>
       
            <div className={classes.control}>
                    <label htmlFor="title">Username </label>
                    <input type="text" required id='user'  ref={user}></input>
                </div>
            <div className={classes.control}>
                    <label htmlFor="title">Password</label>
                    <input type="password" required id='pswd' ref={pswd}></input>
            </div>

            <div className={classes.actions}>
                    <button>Submit</button>
            </div>

        </form>
    </Card>
}

export default Login;
