import Card from "../ui/card";
import classes from './login.module.css';
import loginlogo from './login.png';
import {useRef, useState,useContext} from 'react';
import { useNavigate } from "react-router-dom";

function Login(props){
    const navigate = useNavigate();
    const [state,setState] = useState(true);
    const user = useRef();
    const pswd = useRef();

    

    function submitHandler(event){
        event.preventDefault();

        const User = user.current.value;
        const Pswd = pswd.current.value;

        const Data = {
            Name : User,
            Password : Pswd
        };
        console.log(Data);

        fetch('https://react-9eb50-default-rtdb.firebaseio.com/login.json',
        {
            method:'POST',
            body:JSON.stringify(Data),
            headers:{
                'Content-Type':'application/json'
            }
        }
        ).then((res)=>{
                if (res.ok){
                    setState(true);
                    console.log("ok");
                    navigate("/all",{replace:true});
                }
                else{
                    return res.json().then((data)=>{
                        if (data && data.error && data.error.message){
                            setState(false); //it is main key
                        }
                        setState(false); //just for testing

                    })
                    
                }
        })
                    
            //based on some condition setState()

        
    }

    return <Card>
        <img src={loginlogo}  />
        <form className={classes.form} onSubmit={submitHandler}>
            {state &&<div className={classes.info}>
                <i class="fa fa-info-circle"></i>&nbsp;
                Use SMS credentials to login
            </div>}
            {!state &&<div className={classes.error}>
                <i class="fa fa-times-circle"></i>&nbsp;
                Username or Password is incorret
            </div>}


            <div className={classes.control}>
                    <label htmlFor="title">Username </label>
                    <input type="text" required id='user'  ref={user}></input>
                </div>
            <div className={classes.control}>
                    <label htmlFor="title">Password</label>
                    <input type="password" required id='pswd' ref={pswd}></input>
            </div>

            <div className={classes.actions}>
                    <button>Submit</button>
            </div>

        </form>
    </Card>
}

export default Login;
